var newMetadata = {
  deviceType : metadata.deviceType,
  deviceName : metadata.deviceName,
  ts : metadata.ts
};

var attributeName = [];
attributeName[metadata.ss_macTemperaturaHumedad] = "S1_TH_001";
attributeName[metadata.ss_macTemperaturaHumedad2] = "S1_TH_002";
attributeName[metadata.ss_macTemperaturaHumedad3] = "S1_TH_003";
attributeName[metadata.ss_macTemperaturaHumedad4] = "S1_TH_004";
attributeName[metadata.ss_macTemperaturaHumedad5] = "S1_TH_005";
attributeName[metadata.ss_macPuerta] = "S4_Puerta_001";
attributeName[metadata.ss_macLiquidos] = "S4_Liquidos_002";
attributeName[metadata.ss_macMiniSplit1] = "S4_MiniSplit_001";
attributeName[metadata.ss_macMiniSplit2] = "S4_MiniSplit_002";
attributeName[metadata.ss_macMiniSplit3] = "S4_MiniSplit_003";
attributeName[metadata.ss_macMiniSplit4] = "S4_MiniSplit_004";
attributeName[metadata.ss_macMiniSplit5] = "S4_MiniSplit_005";
attributeName[metadata.ss_macCFE] = "S4_CFE_003";
attributeName[metadata.ss_macCFE2] = "S4_CFE2_003";
attributeName[metadata.ss_macCFE3] = "S4_CFE3_003";
attributeName[metadata.ss_macUPS] = "S4_UPS_004";
attributeName[metadata.ss_macUPS2] = "S4_UPS2_004";
attributeName[metadata.ss_macUPS3] = "S4_UPS3_004";

var newMSG = {};

switch(msg.mac){
  case metadata.ss_macTemperaturaHumedad:
  case metadata.ss_macTemperaturaHumedad2:
  case metadata.ss_macTemperaturaHumedad3:
  case metadata.ss_macTemperaturaHumedad4:
  case metadata.ss_macTemperaturaHumedad5:
    if(msg.type !== 'S1'){
      newMSG = { error: "No coincide tipo de sensor con variable. Revisa la MAC1" };
      break;
    }

    var temperaturaValida = (msg.temperature <= 0 || msg.temperature >= 0.01) ? true : false;
    if(!temperaturaValida){
      newMSG = { error: "Lectura de temperatura errónea" };
      break;
    }

    newMSG[attributeName[msg.mac]+"_rssi"] = msg.rssi;
    newMSG[attributeName[msg.mac]+"_battery"] = msg.battery;
    newMSG[attributeName[msg.mac]+"_temperature"] = msg.temperature;
    newMSG[attributeName[msg.mac]+"_humidity"] = msg.humidity;
    break;
  case metadata.ss_macPuerta:
  case metadata.ss_macLiquidos:
  case metadata.ss_macMiniSplit1:
  case metadata.ss_macMiniSplit2:
  case metadata.ss_macMiniSplit3:
  case metadata.ss_macMiniSplit4:
  case metadata.ss_macMiniSplit5:
  case metadata.ss_macCFE:
  case metadata.ss_macCFE2:
  case metadata.ss_macCFE3:
  case metadata.ss_macUPS:
  case metadata.ss_macUPS2:
  case metadata.ss_macUPS3:
      if(msg.type !== 'S4'){
        newMSG = { error: "No coincide tipo de sensor con variable. Revisa la MAC", mac: msg.mac };
        break;
      }

      newMSG[attributeName[msg.mac]+"_rssi"] = msg.rssi;
      newMSG[attributeName[msg.mac]+"_battery"] = msg.battery;
      newMSG[attributeName[msg.mac]+"_unlocked"] = msg.unlocked;
      newMSG[attributeName[msg.mac]+"_uninstalled"] = msg.uninstalled;
      break;
  default: 
    newMSG = { error: "La MAC registrada no coincide con ningún sensor." };
}

return { msg: newMSG, metadata: newMetadata, msgType: msgType };

var contador = 0;
var temperatura;

for (var clave in msg){
    contador++;
    if (clave === "S1_TH_001_temperature" || clave === "S1_TH_002_temperature" || clave === "S1_TH_003_temperature" || clave === "S1_TH_004_temperature" || clave === "S1_TH_005_temperature"){
        temperatura = msg[clave];
        break;
    }
}


return temperatura >= 0.001 || temperatura <= 0;

var arr = [
    metadata.ss_macTemperaturaHumedad,
    metadata.ss_macTemperaturaHumedad2,
    metadata.ss_macTemperaturaHumedad3,
    metadata.ss_macTemperaturaHumedad4,
    metadata.ss_macTemperaturaHumedad5,
    metadata.ss_macPuerta,
    metadata.ss_macLiquidos,
    metadata.ss_macMiniSplit1,
    metadata.ss_macMiniSplit2,
    metadata.ss_macMiniSplit3,
    metadata.ss_macMiniSplit4,
    metadata.ss_macMiniSplit5,
    metadata.ss_macCFE,
    metadata.ss_macCFE2,
    metadata.ss_macCFE3,
    metadata.ss_macUPS,
    metadata.ss_macUPS2,
    metadata.ss_macUPS3
];

var found = false;

for (var i = 0; i < arr.length; i++) {
  if (arr[i] === msg.mac) {
    found = true;
    break;
  }
}

return found;

var newMetadata = {
  deviceType : metadata.deviceType,
  deviceName : metadata.deviceName,
  ts : metadata.ts
};

var attributeName = [];
attributeName[metadata.ss_macTemperaturaHumedad] = "S1_TH_001";
attributeName[metadata.ss_macTemperaturaHumedad2] = "S1_TH_002";
attributeName[metadata.ss_macTemperaturaHumedad3] = "S1_TH_003";
attributeName[metadata.ss_macTemperaturaHumedad4] = "S1_TH_004";
attributeName[metadata.ss_macTemperaturaHumedad5] = "S1_TH_005";
attributeName[metadata.ss_macPuerta] = "S4_Puerta_001";
attributeName[metadata.ss_macLiquidos] = "S4_Liquidos_002";
attributeName[metadata.ss_macMiniSplit1] = "S4_MiniSplit_001";
attributeName[metadata.ss_macMiniSplit2] = "S4_MiniSplit_002";
attributeName[metadata.ss_macMiniSplit3] = "S4_MiniSplit_003";
attributeName[metadata.ss_macMiniSplit4] = "S4_MiniSplit_004";
attributeName[metadata.ss_macMiniSplit5] = "S4_MiniSplit_005";
attributeName[metadata.ss_macCFE] = "S4_CFE_003";
attributeName[metadata.ss_macCFE2] = "S4_CFE2_003";
attributeName[metadata.ss_macCFE3] = "S4_CFE3_003";
attributeName[metadata.ss_macUPS] = "S4_UPS_004";
attributeName[metadata.ss_macUPS2] = "S4_UPS2_004";
attributeName[metadata.ss_macUPS3] = "S4_UPS3_004";

var newMSG = {};

newMSG[attributeName[msg.mac]+"_rssi"] = msg.rssi;
newMSG[attributeName[msg.mac]+"_battery"] = msg.battery;

if(msg.type === 'S1'){
    newMSG[attributeName[msg.mac]+"_temperature"] = msg.temperature;
    newMSG[attributeName[msg.mac]+"_humidity"] = msg.humidity;
}else{
    newMSG[attributeName[msg.mac]+"_unlocked"] = msg.unlocked;
    newMSG[attributeName[msg.mac]+"_uninstalled"] = msg.uninstalled;
}

return {msg: newMSG, metadata: newMetadata, msgType: msgType};

var attributeName = {};
attributeName[metadata.ss_macTemperaturaHumedad] = "S1";
attributeName[metadata.ss_macTemperaturaHumedad2] = "S1";
attributeName[metadata.ss_macTemperaturaHumedad3] = "S1";
attributeName[metadata.ss_macTemperaturaHumedad4] = "S1";
attributeName[metadata.ss_macTemperaturaHumedad5] = "S1";
attributeName[metadata.ss_macPuerta] = "S4";
attributeName[metadata.ss_macLiquidos] = "S4";
attributeName[metadata.ss_macMiniSplit1] = "S4";
attributeName[metadata.ss_macMiniSplit2] = "S4";
attributeName[metadata.ss_macMiniSplit3] = "S4";
attributeName[metadata.ss_macMiniSplit4] = "S4";
attributeName[metadata.ss_macMiniSplit5] = "S4";
attributeName[metadata.ss_macCFE] = "S4";
attributeName[metadata.ss_macCFE2] = "S4";
attributeName[metadata.ss_macCFE3] = "S4";
attributeName[metadata.ss_macUPS] = "S4";
attributeName[metadata.ss_macUPS2] = "S4";
attributeName[metadata.ss_macUPS3] = "S4";

if(msg.type !== attributeName[msg.mac]){
    return false;
}

if(msg.type === 'S1'){
    var temperaturaValida = (msg.temperature <= 0 || msg.temperature >= 0.01) ? true : false;
    if(!temperaturaValida){
        return false;
    }
}

return true;

